// Bootstrap Bundle Minified - Versão simplificada para o projeto
// Este é um arquivo mínimo que implementa as funcionalidades básicas do Bootstrap

(function() {
    'use strict';
    
    // Collapse functionality
    document.addEventListener('click', function(e) {
        if (e.target.matches('[data-bs-toggle="collapse"]')) {
            e.preventDefault();
            const target = document.querySelector(e.target.getAttribute('data-bs-target'));
            if (target) {
                target.classList.toggle('show');
            }
        }
    });
    
    // Alert dismiss
    document.addEventListener('click', function(e) {
        if (e.target.matches('.btn-close')) {
            e.target.closest('.alert').remove();
        }
    });
    
    // Navbar toggle
    document.addEventListener('click', function(e) {
        if (e.target.matches('.navbar-toggler')) {
            const navbarCollapse = document.querySelector('.navbar-collapse');
            if (navbarCollapse) {
                navbarCollapse.classList.toggle('show');
            }
        }
    });
    
})();
